import * as Yup from "yup";
export const validationSchema = Yup.object({
    // name:  Yup.string().matches(/^[a-zA-Z]+$/, 'Name should contain only alphabets'),
    name: Yup.string().required("Name Required"),
    email: Yup.string().required("Email Required"),
    mobileNumber: Yup.string().required("Mobile Number Required"),
    companyName: Yup.string().required("Company Name  Required"),
    passWord:Yup.string().required("Password  Required"),
});

const nameSchema = Yup.string().matches(/^[a-zA-Z]+$/, 'Name should contain only alphabets');
function validateName(value: string) {
    try {
      nameSchema.validateSync(value);
    } catch (error) {
      return error.message;
    }
  }
