import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField, IconButton, InputAdornment } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import "./SignUpForm.css";
import { validateField, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";
const SignUpForm = () => {
	let navigate = useNavigate();
	const [showPassword, setShowPassword] = useState(false);
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	console.log(formData, "formData");
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		console.log(values, "values");
		dispatch(setUserDetails(values));
		resetForm();
	};

	const handleClickShowPassword = () => {
		setShowPassword(!showPassword);
	};

	const handleMouseDownPassword = (event: any) => {
		event.preventDefault();
	};

	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				<Form>
					{FormFieldsData.map((ele, i) => {
						return (
							<div>
								<Field
									label={ele.label}
									InputLabelProps={{
										style: {
											fontSize: "24px",
											color: "var(--darkPara)",
											fontFamily:
												"Futura-Medium",
										},
									}}
									type={showPassword? "text"
											: "password"
									}
									InputProps={
										ele.name === "passWord" && {
											endAdornment: (
												<InputAdornment position="end">
													<IconButton
														onClick={
															handleClickShowPassword
														}
														onMouseDown={
															handleMouseDownPassword
														}
														edge="end"
													>
														{!showPassword ? (
															<VisibilityOff />
														) : (
															<Visibility />
														)}
													</IconButton>
												</InputAdornment>
											),
										}
									}
									name={ele.name}
									as={TextField}
									fullWidth={ele.fullWidth}
									variant={ele.variant}
									validate={validateField(
										ele.name,
										new StringSchema()
									)}
								/>
								<ErrorMessage
									name={ele.name}
									component="div"
									className="error"
								/>
							</div>
						);
					})}

					<div className="termsConditions">
						{TERMS_CONDITIONS}
					</div>
					<ButtonComponent
						variant="contained"
						type="submit"
						fullWidth={true}
						className="button"
						buttonText={"GET STARTED NOW"}
					/>
				</Form>
			</Formik>
		</div>
	);
};

export default SignUpForm;
