import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField } from "@mui/material";
import "./SignUpForm.css";
import { validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector,useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
const SignUpForm = () => {
	let navigate = useNavigate();
const dispatch=useDispatch()

	const formData=useSelector((state:any)=>state.user)
	console.log(formData,"formData")
	const handleSubmit=(values:UserState,{resetForm}:FormikHelpers<UserState>)=>
	{
		dispatch(setUserDetails(values))
		console.log(values,"values")
		resetForm()
	}
	const handleClick = () => {
		navigate("/getStartedNow");
	};

	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				<Form>
					<Field
						name="name"
						as={TextField}
						label="Name"
						fullWidth
						variant="outlined"
					/>
					<ErrorMessage name="name" component="div" />
					<Field
						name="email"
						as={TextField}
						label="Email"
						fullWidth
						variant="outlined"
					/>
					<ErrorMessage
						name="email"
						component="div"
						className="error"
					/>
					<Field
						name="mobileNumber"
						as={TextField}
						label="Mobile number"
						fullWidth
						variant="outlined"
					/>
					<ErrorMessage
						name="mobileNumber"
						component="div"
						className="error"

					/>
					<Field
						name="companyName"
						as={TextField}
						label="Company name"
						fullWidth
						variant="outlined"
					/>
					<ErrorMessage
						name="companyName"
						component="div"
						className="error"

					/>
					<Field
						name="passWord"
						as={TextField}
						label="Password"
						fullWidth
						variant="outlined"
					/>
					<ErrorMessage
						name="passWord"
						component="div"
						className="error"
					/>
					<div className="termsConditions">
						{TERMS_CONDITIONS}
					</div>
					<ButtonComponent
					   variant=""
						type="submit"
						fullWidth={"fullWidth"}
						className="button"
						onClick={handleClick}
						buttonText={"GET STARTED NOW"}
					/>
				</Form>
			</Formik>
		</div>
	);
};

export default SignUpForm;
