import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField } from "@mui/material";
import "./SignUpForm.css";
import { validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";

const SignUpForm = () => {
	let navigate = useNavigate();
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		console.log(values, "values");
		dispatch(setUserDetails(values));
		resetForm();
	};

	const validateField =
		(fieldName: string, schema: StringSchema) => (value: string) => {
			try {
				schema.validateSync(value);
			} catch (error: any) {
				return error.message;
			}
		};

	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				{({ touched, errors }) => (
					<Form>
						{FormFieldsData.map((ele: any, i) => (
							<div key={i}>
								<Field
									label={ele.label}
									InputLabelProps={{
										style: {
											fontSize: "24px",
											color: "var(--darkPara)",
											fontFamily:
												"Futura-Medium",
										},
									}}
									name={ele.name}
									as={TextField}
									fullWidth={ele.fullWidth}
									variant={ele.variant}
									error={
										[touched.name] &&
										Boolean(errors.name)
									}
									helperText={
										touched.name && errors.name
									}
									validate={validateField(
										ele.name,
										new StringSchema()
									)}
									InputProps={{
										classes: {
											root: "MuiOutlinedInput-root",
											focused: "Mui-focused",
											notchedOutline:
												"Mui-notchedOutline",
											error: "Mui-error",
										},
									}}
								/>
							</div>
						))}

						<div className="termsConditions">
							{TERMS_CONDITIONS}
						</div>
						<ButtonComponent
							variant="contained"
							type="submit"
							fullWidth={true}
							className="button"
							buttonText={"GET STARTED NOW"}
						/>
					</Form>
				)}
			</Formik>
		</div>
	);
};

export default SignUpForm;
