import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./styles.css";

function GridItem({ src, hoverColor }) {
  const [opacity, setOpacity] = React.useState(1);

  const handleMouseEnter = () => {
    setOpacity(0.7);
  };

  const handleMouseLeave = () => {
    setOpacity(1);
  };

  return (
    <div className="grid-item" style={{ opacity }}>
      <img
        src={src}
        alt="Grid Item"
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
        style={{ backgroundColor: hoverColor }}
      />
    </div>
  );
}

function CarouselGrid() {
  const images = [
    {
      src: "image1.jpg",
      hoverColor: "#F44336",
    },
    {
      src: "image2.jpg",
      hoverColor: "#E91E63",
    },
    {
      src: "image3.jpg",
      hoverColor: "#9C27B0",
    },
    {
      src: "image4.jpg",
      hoverColor: "#673AB7",
    },
    {
      src: "image5.jpg",
      hoverColor: "#3F51B5",
    },
    {
      src: "image6.jpg",
      hoverColor: "#2196F3",
    },
    {
      src: "image7.jpg",
      hoverColor: "#00BCD4",
    },
    {
      src: "image8.jpg",
      hoverColor: "#4CAF50",
    },
    {
      src: "image9.jpg",
      hoverColor: "#FFEB3B",
    },
    {
      src: "image10.jpg",
      hoverColor: "#FF9800",
    },
    {
      src: "image11.jpg",
      hoverColor: "#795548",
    },
    {
      src: "image12.jpg",
      hoverColor: "#9E9E9E",
    },
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 6,
    slidesToScroll: 6,
    autoplay: true,
    autoplaySpeed: 2000,
    rtl: true,
  };

  return (
    <div className="gridContainer">
      <div className="carouselContainer">
        <Slider {...settings}>
          {images.slice(0, 6).map((image) => (
            <GridItem
              key={image.src}
              src={image.src}
              hoverColor={image.hoverColor}
            />
          ))}
        </Slider>
      </div>
      <div className="carousel-container">
        <Slider {...settings}>
          {images.slice(6).map((image) => (
            <GridItem
              key={image.src}
              src={image.src}
              hoverColor={image.hoverColor}
            />
          ))}
        </Slider>
      </div>
    </div>
  );
}

export default CarouselGrid;
