import * as Yup from "yup";

  export const validationSchema = {
    name: Yup.string().matches(/^[a-zA-Z]+$/, 'Name should contain only alphabets'),
    email: Yup.string().email('Invalid email'),
    mobileNumber: Yup.string().matches(/^[0-9]{10}$/, 'Invalid mobile number'),
    password: Yup.string().min(8, 'Password should be at least 8 characters long'),
    companyName: Yup.string().required("Company Name  Required"),

  };

  function validateField(name: string, value: string) {
    try {
      validationSchema[name].validateSync(value);
    } catch (error:string) {
      return error.message;
    }
  }
