import React,{useState} from "react";
import "./Card.css";
import { Link } from "react-router-dom";
interface featuresCardProps {
	heading: string;
	subHeading: Array<string>;
	featuresText: string;
	buttonText: string;
	path: string;
	id: Number;
	features: Array<string>;
}
const FeaturesCardComponent: React.FC<featuresCardProps> = ({
	heading,
	subHeading,
	featuresText,
	features,
	buttonText,
	path,
	id,
}) => {
	console.log(id, "id");
const[onHover,setOnHover]=useState(false)
	return (
		<div className={onHover ? "card2Contents" : "sanerNowCardContents"}>
			<div className={onHover? "card2ContentsHeading" : "heading"}>
				{heading}
			</div>
			<div className={id === 4 ? "card2SubContents" : "subContents"}>
				{subHeading.map((ele, i) => {
					return (
						<div
							className={
								id === 2
									? "card2ContentsSubHeadings"
									: "sub-headings"
							}
						>
							<span>{ele}</span>
						</div>
					);
				})}
			</div>
			<div
				className={
					id === 2 ? "card2ContentsFeaturesText" : "featuresText"
				}
			>
				{featuresText}
			</div>
			<div className="features">
				{features.map((ele, i) => {
					return (
						<div
							className={
								id === 2
									? "card2ContentsFeatures"
									: "contents"
							}
						>
							<div
								className={
									id === 2
										? "card2Circle"
										: "circle-with-tick"
								}
							></div>
							{/* {<CheckCircleOutlineIcon />} */}
							<span>{ele}</span>
						</div>
					);
				})}
			</div>
			<div className={id === 2 ? "card2Button" : "buttons"}>
				<Link
					className={
						id === 2
							? "card2ButtonContentSection"
							: "buttonContentSection"
					}
					to={path}
				>
					{buttonText}
				</Link>
			</div>
		</div>
	);
};

export default FeaturesCardComponent;
