import React from "react";
import styles from "./Footer.module.css";
import {
	ENDORSEMENTS_HEADER_TEXT,
	ENDORSEMENTS_SUB_HEADING,
} from "../../utils/cms";
import { FooterData } from "./data";
import { Link } from "react-router-dom";
import ButtonComponent from "../ButtonComponent/ButtonComponent";
import { useNavigate } from "react-router-dom";
import linkendInLogo from "../../assets/images/linkedin.png";
import instagram from "../../assets/images/instagram.png";
import twitter from "../../assets/images/twitter.png";
import facebook from "../../assets/images/facebook.png";
import brandLogoImage1 from '../../'
function Footer() {
	let navigate = useNavigate();
	const handleClick = () => {
		// e.preventDefault();
		navigate("/support");
	};
	return (
		<div className={styles.footer}>
			<div className={styles.endorsement}>
				<div className={styles.contents}>
					<div className={styles.header}>
						{ENDORSEMENTS_HEADER_TEXT}
					</div>
					<div className={styles.subHeader}>
						{ENDORSEMENTS_SUB_HEADING}
					</div>
				</div>
				<div className={styles.logoBackground}>


				</div>
				{/* need to do  looping here */}
				{/* <div className={styles.logoBackground}>
					<img src={brandLogo} alt=""/>
				</div>
				<div className={styles.logoBackground}>
				<img src={brandLogo} alt=""/>

				</div>
				<div className={styles.logoBackground}>
				<img src={brandLogo} alt=""/>

				</div>
				<div className={styles.logoBackground}>
				<img src={brandLogo} alt=""/>

				</div> */}
			</div>

			<div className={styles.footerSections}>
				{FooterData.map((ele, i) => {
					return (
						<div className={styles.footerData}>
							<div className={styles.footerHeading}>
								{ele.heading}
							</div>
							<ul>
								{ele.contents.map((e, i) => {
									return (
										<Link to={e.path}>
											<li>{e.subHeadings}</li>
										</Link>
									);
								})}
							</ul>
						</div>
					);
				})}
				<div className={styles.footerButton}>
					<ButtonComponent
						buttonText={"Support"}
						onClick={handleClick}
						className={styles.footerButtonText}
					/>
					<div className={styles.followUs}>Follow US</div>
					<div className={styles.socialMediaLogos}>
						{/* maintain one array */}
						<Link to="https://www.linkedin.com/company/secpod-technologies/">
							<img src={linkendInLogo} alt="" />
						</Link>

						<Link to="https://www.instagram.com/secpodtechnologies/?igshid=1lc509npie1ac">
							<img src={instagram} alt="" />
						</Link>
						<Link to="https://twitter.com/secpod">
							<img src={twitter} alt="" />
						</Link>
						<Link to="https://www.facebook.com/secpod">
							<img src={facebook} alt="" />
						</Link>
					</div>
					<div className={styles.copyRight}>
						Copyright 2023-SanerNow
					</div>
				</div>
			</div>
		</div>
	);
}

export default Footer;
