import React, { useState } from "react";
import "./Card.css";
import { Link } from "react-router-dom";

interface featuresCardProps {
  heading: string;
  subHeading: Array<string>;
  featuresText: string;
  buttonText: string;
  path: string;
  id: Number;
  features: Array<string>;
}

const FeaturesCardComponent: React.FC<featuresCardProps> = ({
  heading,
  subHeading,
  featuresText,
  features,
  buttonText,
  path,
  id,
}) => {
  const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const cardStyle = {
    backgroundColor: isHovered ? "#eee" : "#fff",
    transition: "background-color 0.5s ease-in-out"
  };

  const headingStyle = {
    color: isHovered ? "#333" : "#666",
    transition: "color 0.5s ease-in-out"
  };

  const subheadingStyle = {
    color: isHovered ? "#666" : "#999",
    transition: "color 0.5s ease-in-out"
  };

  const featuresTextStyle = {
    color: isHovered ? "#333" : "#666",
    transition: "color 0.5s ease-in-out"
  };

  const featuresStyle = {
    display: isHovered ? "block" : "none",
    transition: "display 0.5s ease-in-out"
  };

  return (
    <div
      className="sanerNowCardContents"
      style={cardStyle}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="heading" style={headingStyle}>
        {heading}
      </div>
      <div className={id === 4 ? "card2SubContents" : "subContents"}>
        {subHeading.map((ele, i) => {
          return (
            <div className="sub-headings" style={subheadingStyle}>
              <span>{ele}</span>
            </div>
          );
        })}
      </div>
      <div className="featuresText" style={featuresTextStyle}>
        {isHovered ? "Hovered Text" : featuresText}
      </div>
      <div className="features" style={featuresStyle}>
        {features.map((ele, i) => {
          return (
            <div className="contents">
              <div className="circle-with-tick"></div>
              {/* {<CheckCircleOutlineIcon />} */}
              <span>{ele}</span>
            </div>
          );
        })}
      </div>
      <div className="buttons">
        <Link className="buttonContentSection" to={path}>
          {buttonText}
        </Link>
      </div>
    </div>
  );
};

export default FeaturesCardComponent;
