import React, { useState } from "react";
import "./Card.css";
import { Link } from "react-router-dom";
interface featuresCardProps {
	heading: string;
	subHeading: Array<string>;
	featuresText: string;
	buttonText: string;
	path: string;
	id: Number;
	features: Array<string>;
}
const FeaturesCardComponent: React.FC<featuresCardProps> = ({
	heading,
	subHeading,
	featuresText,
	features,
	buttonText,
	path,
	id,
}) => {
	console.log(id, "id");
	const [isHovered, setIsHovered] = useState(false);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

	return (
		<div className={"sanerNowCardContents"}>
			<div className={"heading"}>{heading}</div>
			<div className={id === 4 ? "card2SubContents" : "subContents"}>
				{subHeading.map((ele, i) => {
					return (
						<div className={"sub-headings"}>
							<span>{ele}</span>
						</div>
					);
				})}
			</div>
			<div className={"featuresText"}>{featuresText}</div>
			<div className="features">
				{features.map((ele, i) => {
					return (
						<div className={"contents"}>
							<div className={"circle-with-tick"}></div>
							{/* {<CheckCircleOutlineIcon />} */}
							<span>{ele}</span>
						</div>
					);
				})}
			</div>
			<div className={"buttons"}>
				<Link className={"buttonContentSection"} to={path}>
					{buttonText}
				</Link>
			</div>
		</div>
	);
};

export default FeaturesCardComponent;
