import React,{useState} from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField } from "@mui/material";
import "./SignUpForm.css";
import { validateField, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";
const SignUpForm = () => {
	let navigate = useNavigate();
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	console.log(formData, "formData");
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		console.log(values, "values");
		dispatch(setUserDetails(values));
		resetForm();
	};
	// const handleClick = () => {
	// 	navigate("/getStartedNow");
	// };
	const [isHovered, setIsHovered] = useState(false);

	const handleMouseEnter = () => {
		setIsHovered(true);
	};

	const handleMouseLeave = () => {
		setIsHovered(false);
	};

	const cardStyle = {
		borderColor: isHovered ? "#012f51" : "",
		boxShadow: isHovered
			? "0 2px 10px 2px rgba(2, 15, 28, 0.3)"
			: "0 2px 10px 2px rgba(100, 100, 100, 0.3)",
		transition: "background-color 0.5s ease-in-out",
	};
	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				<Form>
					{FormFieldsData.map((ele, i) => {
						return (
							<div>
								<Field
									label={ele.label}
									InputLabelProps={{
										style: {
											fontSize: "24px",
											color: "var(--darkPara)",
											fontFamily:
												"Futura-Medium",
										},
									}}
									onMouseEnter={handleMouseEnter}
			              onMouseLeave={handleMouseLeave}
						  style={cardStyle}

									name={ele.name}
									as={TextField}
									fullWidth={ele.fullWidth}
									variant={ele.variant}
									validate={validateField(
										ele.name,
										new StringSchema()
									)}
								/>
								<ErrorMessage
									name={ele.name}
									component="div"
									className="error"
								/>
							</div>
						);
					})}

					<div className="termsConditions">
						{TERMS_CONDITIONS}
					</div>
					<ButtonComponent
						variant="contained"
						type="submit"
						fullWidth={true}
						className="button"
						buttonText={"GET STARTED NOW"}
					/>
				</Form>
			</Formik>
		</div>
	);
};

export default SignUpForm;
