import React, { useState } from "react";
import { Form, Field, ErrorMessage, FormikHelpers, Formik, FieldProps } from "formik";
import { TextField, IconButton, InputAdornment, Checkbox } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import "./SignUpForm.css";
import { validateField, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";

const SignUpForm = () => {
  const [showPassword, setShowPassword] = useState(false);
  const dispatch = useDispatch();
  const formData = useSelector((state: any) => state.user);

  const handleSubmit = (values: UserState, { resetForm }: FormikHelpers<UserState>) => {
    dispatch(setUserDetails(values));
    resetForm();
  };

  const handleClickShowPassword = () => {
    setShowPassword(!showPassword);
  };

  const handleMouseDownPassword = (event: any) => {
    event.preventDefault();
  };

  return (
    <div className="signUpFormContents">
      <Formik 
        initialValues={formData}
        validationSchema={validationSchema}
        onSubmit={handleSubmit}
      >
        {({ errors, touched, handleBlur, isSubmitting }: FieldProps<UserState>) => (
          <Form>
            {FormFieldsData.map((ele, i) => {
              return (
                <div key={i}>
                  <Field
                    label={ele.label}
                    error={touched.name && Boolean(errors.name)}
                    onBlur={handleBlur}
                    helperText={touched.name && errors.name}
                    InputLabelProps={{
                      style: {
                        fontSize: "24px",
                        color: "var(--darkPara)",
                        fontFamily: "Futura-Medium",
                      },
                    }}
                    type={
                      ele.name === "passWord" &&
                      (showPassword ? "text" : "password")
                    }
                    InputProps={
                      ele.name === "passWord" && {
                        style: {
                          borderColor: errors.name && 'red',
                          color: errors.name && 'red',
                        },
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              onClick={handleClickShowPassword}
                              onMouseDown={handleMouseDownPassword}
                              edge="end"
                            >
                              {!showPassword ? (
                                <VisibilityOff />
                              ) : (
                                <Visibility />
                              )}
                            </IconButton>
                          </InputAdornment>
                        ),
                      }
                    }
                    name={ele.name}
                    as={TextField}
                    fullWidth={ele.fullWidth}
                    variant={ele.variant}
                    validate={validateField(
                      ele.name,
                      new StringSchema()
                    )}
                  />
                  {/* <ErrorMessage
                      name={ele.name}
                      component="div"
                      className="error"
                    /> */}
                </div>
              );
            })}

            <div className="termsConditions">
              <Checkbox
                defaultChecked
                sx={{
                  "& .MuiSvgIcon-root": { fontSize: 28 },
                }}
              />
              {TERMS_CONDITIONS}
            </div>
            <ButtonComponent
              variant="contained"
              type="submit"
              fullWidth={true}
              className="button"
              buttonText={"GET STARTED NOW"}
            />
          </Form>
        )}
      </Formik>
    </div>
  );
};

export default SignUpForm;
