import Slider from 'react-slick';
import styles from './carousel.module.css';
import { AiOutlineRight, AiOutlineLeft } from 'react-icons/ai';

type carouselType = {
    image: string;

};

type carouselMainDataType = {
    carouselData: carouselType[];
    singleCarousel: boolean;
};

type arrowDataType = {
    onClick?: () => void;
};

function NextArrow({ onClick }: arrowDataType): JSX.Element {
    return (
        <div
            className={`${styles.arrow} ${styles.right}`}
            onClick={onClick}
        >
            <AiOutlineRight />
        </div>
    );
}

function PrevArrow({ onClick }: arrowDataType): JSX.Element {
    return (
        <div
            className={`${styles.arrow} ${styles.left}`}
            onClick={onClick}
        >
            <AiOutlineLeft />
        </div>
    );
}

export default function Carousel({ carouselData, singleCarousel,  }: carouselMainDataType): JSX.Element {

    const settings = {
        dots: singleCarousel,
        infinite: true,
        arrow: true,
        autoplay: singleCarousel,
        speed: 500,
        slidesToShow: singleCarousel ? 1 : 6,
        slidesToScroll: 1,
        initialSlide: 5,
        nextArrow: singleCarousel ? <></> : <NextArrow />,
        prevArrow: singleCarousel ? <></> : <PrevArrow />,
        responsive: singleCarousel ? [] : [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 4,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 900,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                    initialSlide: 3,
                },
            },
            {
                breakpoint: 700,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
            {
                breakpoint: 500,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                },
            },
        ],
    };

    return (
        <div className={styles.carouselContainer}>
            <Slider {...settings}>
                {carouselData.map((data: carouselType, i) =>
                    <div className={styles.carouselCard} key={i}>
                        <img src={data.image} alt="" />

                    </div>
                )}
            </Slider>
        </div>
    );
};
