import { takeEvery, call, put } from "redux-saga/effects";
import {  setUserDetails } from "../redux/features/formSlice";
import { PRODUCTS_API } from "../services/masters";
interface responseData {
	json: any;
}
function* userSaga() {
	try {
		const response: responseData = yield call(() =>fetch(PRODUCTS_API));
		const responseData: responseData = yield response.json();
		yield put(setSuccessData(responseData));
	} catch (error: any) {
		console.log(error.message);
	}
}
function* rootSaga() {
	yield takeEvery(setUserDetails, userSaga);
}
export default rootSaga;
