import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField } from "@mui/material";
import "./SignUpForm.css";
import { validateName, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import * as Yup from "yup";

const SignUpForm = () => {
	let navigate = useNavigate();
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	console.log(formData, "formData");
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		console.log(values, "values");
		dispatch(setUserDetails(values));
		resetForm();
	};
	// const handleClick = () => {
	// 	navigate("/getStartedNow");
	// };



	const validationSchema:any = {
		name: Yup.string().matches(/^[a-zA-Z]+$/, 'Name should contain only alphabets'),
		email: Yup.string().email('Invalid email'),
		mobileNumber: Yup.string().matches(/^[0-9]{10}$/, 'Invalid mobile number'),
		password: Yup.string().min(8, 'Password should be at least 8 characters long'),
	  };

	  function validateField(name: string, value: string) {
		try {
		  validationSchema[name].validateSync(value);
		} catch (error:any) {
		  return error.message;
		}
	  }


	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				<Form>
					{FormFieldsData.map((ele, i) => {
						return (
							<div>
								<Field
									label={ele.label}
									name={ele.name}
									as={TextField}
									fullWidth={ele.fullWidth}
									variant={ele.variant}
									validate={validateName(ele.name)}
								/>
								<ErrorMessage
									name={ele.name}
									component="div"
									className="error"
								/>
							</div>
						);
					})}

					<div className="termsConditions">
						{TERMS_CONDITIONS}
					</div>
					<ButtonComponent
						variant="contained"
						type="submit"
						fullWidth={true}
						className="button"
						buttonText={"GET STARTED NOW"}
					/>
				</Form>
			</Formik>
		</div>
	);
};

export default SignUpForm;
