import React, { useState } from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField, IconButton, InputAdornment, Checkbox } from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import "./SignUpForm.css";
import { validateField, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";
const SignUpForm = () => {
	const [showPassword, setShowPassword] = useState(false);
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		dispatch(setUserDetails(values));
		resetForm();
	};

	const handleClickShowPassword = () => {
		setShowPassword(!showPassword);
	};

	const handleMouseDownPassword = (event: any) => {
		event.preventDefault();
	};

	return (
		<div className="signUpFormContents">
		<Formik
  initialValues={{ name: '', email: '', password: '' }}
  validationSchema={validationSchema}
  onSubmit={handleSubmit}
>
  {({ errors, touched, handleChange, handleBlur, isSubmitting }) => (
    <Form>
      <Field
        name="name"
        as={TextField}
        label="Name"
        error={touched.name && Boolean(errors.name)}
        helperText={touched.name && errors.name}
        variant="outlined"
        margin="normal"
        fullWidth
        onChange={handleChange}
        onBlur={handleBlur}
        InputProps={{
          style: {
            borderColor: errors.name && 'red',
            color: errors.name && 'red',
          },
        }}
      />
      <Field
        name="email"
        as={TextField}
        label="Email"
        error={touched.email && Boolean(errors.email)}
        helperText={touched.email && errors.email}
        variant="outlined"
        margin="normal"
        fullWidth
        onChange={handleChange}
        onBlur={handleBlur}
        InputProps={{
          style: {
            borderColor: errors.email && 'red',
            color: errors.email && 'red',
          },
        }}
      />
      <Field
        name="password"
        as={TextField}
        label="Password"
        type="password"
        error={touched.password && Boolean(errors.password)}
        helperText={touched.password && errors.password}
        variant="outlined"
        margin="normal"
        fullWidth
        onChange={handleChange}
        onBlur={handleBlur}
        InputProps={{
          style: {
            borderColor: errors.password && 'red',
            color: errors.password && 'red',
          },
        }}
      />
      <Button
        type="submit"
        variant="contained"
        color="primary"
        disabled={isSubmitting}
      >
        Submit
      </Button>
    </Form>
  )}
</Formik>

		</div>
	);
};

export default SignUpForm;
