import React from "react";
import { Formik, Form, Field, ErrorMessage, FormikHelpers } from "formik";
import { TextField,IconButton, InputAdornment  } from "@mui/material";
import "./SignUpForm.css";
import { validateField, validationSchema } from "./SignUpValidationSchema";
import { TERMS_CONDITIONS } from "../../utils/cms";
import { useNavigate } from "react-router-dom";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import { useSelector, useDispatch } from "react-redux";
import { setUserDetails, UserState } from "../../redux/features/formSlice";
import { FormFieldsData } from "./data";
import { StringSchema } from "yup";





const PasswordField = () => {

  return (
    <TextField
      label="Password"
      type={showPassword ? 'text' : 'password'}
      InputProps={{
        endAdornment: (
          <InputAdornment position="end">
            <IconButton onClick={handleTogglePassword}>
              {showPassword ? <VisibilityOff /> : <Visibility />}
            </IconButton>
          </InputAdornment>
        ),
      }}
    />
  );
};

export default PasswordField;

const SignUpForm = () => {
	let navigate = useNavigate();
	const dispatch = useDispatch();
	const formData = useSelector((state: any) => state.user);
	console.log(formData, "formData");
	const handleSubmit = (
		values: UserState,
		{ resetForm }: FormikHelpers<UserState>
	) => {
		console.log(values, "values");
		dispatch(setUserDetails(values));
		resetForm();
	};
	// const handleClick = () => {
	// 	navigate("/getStartedNow");
	// };

	return (
		<div className="signUpFormContents">
			<Formik
				initialValues={formData}
				validationSchema={validationSchema}
				onSubmit={handleSubmit}
			>
				{({ errors, touched }) => (
					<Form>
						{FormFieldsData.map((ele, i) => {
							return (
								<div>
									<Field
										label={ele.label}
										InputLabelProps={{
											style: {
												fontSize: "24px",
												color: "var(--darkPara)",
												fontFamily:
													"Futura-Medium",
											},
										}}
										name={ele.name}
										as={TextField}
										fullWidth={ele.fullWidth}
										variant={ele.variant}
										validate={validateField(
											ele.name,
											new StringSchema()
										)}
									/>
									<ErrorMessage
										name={ele.name}
										component="div"
										className="error"
									/>
								</div>
							);
						})}

						<div className="termsConditions">
							{TERMS_CONDITIONS}
						</div>
						<ButtonComponent
							variant="contained"
							type="submit"
							fullWidth={true}
							className="button"
							buttonText={"GET STARTED NOW"}
						/>
					</Form>
				)}
			</Formik>
		</div>
	);
};

export default SignUpForm;
