import { call, put, takeLatest } from 'redux-saga/effects';
import axios from 'axios';

function* signInUser(action) {
  try {
    const response = yield call(axios.post, 'http://localhost:3000/sanernow/api/users/signin', action.payload);
    // If the API call is successful, dispatch a success action with the response data
    yield put({ type: 'SIGNIN_USER_SUCCESS', payload: response.data });
  } catch (error) {
    // If the API call fails, dispatch a failure action with the error message
    yield put({ type: 'SIGNIN_USER_FAILURE', payload: error.message });
  }
}

function* signInUserSaga() {
  yield takeLatest('SIGNIN_USER_REQUEST', signInUser);
}

export default signInUserSaga;
