import Slider from "react-slick";
import styles from "./carousel.module.css";
import { AiOutlineRight, AiOutlineLeft } from "react-icons/ai";

type carouselType = {
	image: string;
};

type carouselMainDataType = {
	carouselData: carouselType[];
};

type arrowDataType = {
	onClick?: () => void;
};

function NextArrow({ onClick }: arrowDataType): JSX.Element {
	return (
		<div className={`${styles.arrow} ${styles.right}`} onClick={onClick}>
			<AiOutlineRight />
		</div>
	);
}

function PrevArrow({ onClick }: arrowDataType): JSX.Element {
	return (
		<div className={`${styles.arrow} ${styles.left}`} onClick={onClick}>
			<AiOutlineLeft />
		</div>
	);
}

export default function Carousel({
	carouselData,
}: carouselMainDataType): JSX.Element {
	const settings = {
		dots: false,
		arrow: true,
		autoplay: true,
		speed: 500,
		slidesToShow: 6,
		slidesToScroll: 1,
		initialSlide: 5,
		nextArrow: <NextArrow />,
		prevArrow: <PrevArrow />,
	};

	return (
		<div className={styles.carouselContainer}>
			<Slider {...settings}>
				{carouselData.map((data: carouselType, i) => (
					<div className={styles.carouselCard} key={i}>
						<img src={data.image} alt="" />
					</div>
				))}
			</Slider>
		</div>
	);
}
