import axios from "axios";
import { takeEvery, call, put } from "redux-saga/effects";
import { SIGNUP_API } from "../config /config";
import { setUserDetails } from "../redux/features/formSlice";

interface responseData {
	json: any;
	data:any;
}
function* userSaga(action:any) {
	try {
		const response: responseData = yield call(axios.post,SIGNUP_API,action.payload);
        const responseData: responseData = yield response.json();
		console.log(response,"responseData")

		// yield put(setSuccessData(responseData));
	} 
	catch (error: any) {
		console.log(error.message,"error");
	}
}
function* rootSaga() {
	yield takeEvery(setUserDetails, userSaga);
}
export default rootSaga;
