import { call, put, takeLatest } from "redux-saga/effects";
import axios from "axios";
import { SIGNUP_API } from "./constants";
import { signupRequested, signupSuccess, signupFailed } from "./signupSlice";

function* signupUser(action) {
  yield put(signupRequested());
  try {
    const response = yield call(axios.post, SIGNUP_API, action.payload);
    yield put(signupSuccess(response.data.message));
  } catch (error) {
    yield put(signupFailed(error.response.data.message));
  }
}

export function* watchSignup() {
  yield takeLatest("signup/signupUser", signupUser);
}
