import * as Yup from "yup";
const nameSchema = Yup.string().matches(/^[a-zA-Z]+$/, 'Name should contain only alphabets');
const emailSchema = Yup.string().email('Invalid email address');
const mobileSchema = Yup.string().matches(/^(\+?\d{1,4}[\s-])?(?!0+\s+,?$)\d{10}\s*,?$/, 'Invalid mobile number');
const passwordSchema = Yup.string().min(6, 'Password should be at least 6 characters long');

function validateField(value: string, schema: Yup.StringSchema): string | undefined {
  try {
    schema.validateSync(value);
  } catch (error:any) {
    return error.message;
  }
}

function InputForm() {
    const validationSchema = Yup.object({
      name: nameSchema.required('Name is required'),
      email: emailSchema.required('Email is required'),
      mobile: mobileSchema.required('Mobile number is required'),
      password: passwordSchema.required('Password is required'),
    });
}
