					<div className="termsConditions">
						{TERMS_CONDITIONS}
					</div>
					<ButtonComponent
						variant="contained"
						type="submit"
						fullWidth={true}
						className="button"
						buttonText={"GET STARTED NOW"}
					/>
				</Form>
			</Formik>
		</div>
	);
};

export default SignUpForm;
